    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]


  const Header = () => {
    return (
      <div> 
    <p> {course} </p>
    </div> 
    )
  }
  const Content = (props) => {
   return(

      <div> 

 <p>{props[0].name} {props[0].exercises} </p>
 <p>{props[1].name} {props[1].exercises} </p>
 <p>{props[2].name} {props[2].exercises} </p>

    </div> 

   )
  }

  const Total = (props) => {
    let i = 0
    return (
      <div> 
    <p> Number of exercises {parts.forEach(element => {
      i += element.exercises
    return (i)
   })}   </p>




    </div> 
    )
    }

    const Part = (props) => {
      return (
        <div> 
      <p> {props.tehtävä} {props.määrä}</p>




      </div> 
      )
      }



